import React from 'react';

export class Login extends React.Component<any, any> {
  constructor(props: any) {
    super(props);
    this.state = {
      username: '',
      password: '',
    };
  }
  // handlechange == changes the state
  handleChange(event) {
    
  }
  // handlesubmit == submits the data -- this posts data to backend -- in this case logs the data
  handleSubmit() {}
  render() {
    return (
      <>
        <header>
          <h1 className="login-title">Undersea</h1>
        </header>
        <div>
          <h2 className="form-title">Belépés</h2>
        </div>
        <form onSubmit={this.handleSubmit}>
          <div>
            <input
              type="text"
              name="username"
              placeholder="Felhasználónév"
              onChange={this.handleChange}
              required
            />
          </div>
          <div>
            <input
              type="password"
              name="password"
              placeholder="Jelszó"
              onChange={this.handleChange}
              required
            />
          </div>
          <div>
            <button onSubmit={this.handleSubmit}>Belépés</button>
          </div>
        </form>

        <div>
          <a className="reg-button" href="./registration">
            Regisztráció
          </a>
        </div>
      </>
    );
  }
}
