import React from 'react';
import { Link } from 'react-router-dom';

export class Login extends React.Component<any, any> {
  constructor(props: any) {
    super(props);
    this.state = {
      username: '',
      password: '',
    };
  }
  // handlechange == changes the state
  handleChange(event: any) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }
  // handlesubmit == submits the data -- this posts data to backend -- in this case logs the data
  handleSubmit(event: any) {}
  render() {
    return (
      <>
        <header>
          <h1 className="login-title">UNDERSEA</h1>
        </header>
        <div className="form">
          <h2 className="form-title">Belépés</h2>
          <form onSubmit={this.handleSubmit}>
            <div>
              <input
                type="text"
                name="username"
                //TODO ?
                placeholder="Fe2lhasználónév"
                onChange={event => this.handleChange(event.target.value)}
                required
              />
            </div>
            <div>
              <input
                type="password"
                name="password"
                //TODO ?
                placeholder="Jelszó"
                onChange={this.handleChange}
                required
              />
            </div>
            <div>
              <input
                className="submit-button"
                type="submit"
                onSubmit={this.handleSubmit}
                value="Belépés"
              />
            </div>
          </form>

          <div>
            <Link className="reg-button" to="./register">
              Regisztráció
            </Link>
          </div>
        </div>
      </>
    );
  }
}
